import LevelBuilderfrom sprites import *def render(name,bg):	lb = LevelBuilder.LevelBuilder(name+".plist",background=bg)	lb.addObject(Hero.HeroSprite(x=16, y=16,width=32,height=32))
	lb.addObject(Bullet.BulletSprite(x=0, y=0,width=10,height=10,angle='0',restitution=0.5,static='false',friction=0.5,density=3,spawnEvent='onShoot'))	lb.addObject(Friend.FriendSprite(x=2600, y=36,width=73,height=73,angle='0',restitution=0.5,static='false',friction=0.1,density=1 , firstframe ='wheel.png' ).setName('wheel'))	lb.addObject(Joints.RevoluteJoint(body1='wheel',body2='spaceship',motor_speed='-50',enable_motor='true',torque='10000',lower_angle='12',upper_angle='50',userData='rev_joint',enable_limit='false',collide_connected='false'))	lb.addObject(Beam.BeamSprite(x=1841, y=0,width=127,height=14,angle='-15' ,restitution=0.2,static='true',friction=0.5,density=20 ).setName('Beam'))	lb.addObject(Friend.FriendSprite(x=2516, y=100,width=77,height=77,angle='0',restitution=0.2,static='false',friction=0.1,density=6 , firstframe ='sprocket.png' ).setName('wheel2'))	lb.addObject(Joints.RevoluteJoint(body1='wheel2',body2='spaceship',motor_speed='-500',enable_motor='true',torque='10000',lower_angle='12',upper_angle='50',userData='rev_joint',enable_limit='false',collide_connected='false'))	lb.addObject(Friend.FriendSprite(x=2680, y=100,width=77,height=77,angle='0',restitution=0.2,static='false',friction=0.1,density=6 , firstframe ='sprocket.png' ).setName('wheel3'))	lb.addObject(Joints.RevoluteJoint(body1='wheel3',body2='spaceship',motor_speed='500',enable_motor='true',torque='10000',lower_angle='12',upper_angle='50',userData='rev_joint',enable_limit='false',collide_connected='false'))	#lb.addObject(Hero.HeroSprite(x=67, y=55,width=32,height=32))
	lb.addObject(Beam.BeamSprite(x=2599, y=115,width=360,height=159,angle='0' ,restitution=0.1,static='false',friction=0,density=3 , firstframe ='spaceship.png', classname='SpaceshipSprite' ).setName('spaceship'))	lb.addObject(Contacts.Contact(body1='spaceship',body2='lbullet',event_name='onBulletHit'))
	lb.addObject(Contacts.Contact(body1='spaceship',body2='rbullet',event_name='onBulletHit'))
	lb.addObject(Enemy.EnemySprite(x=0, y=0,width=32,height=32,angle='0',restitution=0.5,static='false',friction=0.5,density=20, firstframe='slingerball.png', classname='SpaceshipBombSprite' ,spawnEvent='onSpaceshipShoot'))
	
	lb.addObject(Teleporter.TeleporterSprite(level_id='leveldata/level_1'))
	lb.render()