import LevelBuilderfrom sprites import *def render(name,bg):	lb = LevelBuilder.LevelBuilder(name+".plist",background=bg)	lb.addObject(Bullet.BulletSprite(x=0, y=0,width=10,height=10,angle='0',restitution=0.5,static='false',friction=0.5,density=3,spawnEvent='onShoot'))	lb.addObject(Hero.HeroSprite(x=130, y=41,width=42,height=74))	lb.addObject(Alert.Alert(x=149,y=250,width=100,height=500,name='ImageAlert',msg='tilt_move.png'))	lb.addObject(Beam.BeamSprite(x=1103, y=64,width=128,height=30,angle='90' ,restitution=0.2,static='true',friction=0.5,density=20 , firstframe ='bar_long.png' ).setName('Beam'))	lb.addObject(Alert.Alert(x=430,y=250,width=100,height=500,name='ImageAlert',msg='left_touch.png'))	lb.addObject(Alert.Alert(x=2552,y=250,width=100,height=500,name='ImageAlert',msg='walk_tunnel.png'))
		lb.addObject(EventTrigger.EventTrigger(x=2665,y=250,width=100,height=500,msg='onLevelCleared',eventName='onEventTriggerHit'))	lb.addObject(Beam.BeamSprite(x=1677, y=106,width=213,height=30,angle='90' ,restitution=0.2,static='true',friction=0.5,density=20 , firstframe ='bar_long.png' ).setName('Beam'))	lb.addObject(Alert.Alert(x=860,y=161,width=100,height=320,msg='press longer#to jump higher'))	lb.addObject(Teleporter.TeleporterSprite( level_id='leveldata/level_1_2'))	lb.addObject(Beam.BeamSprite(x=651, y=16,width=88,height=30,angle='0' ,restitution=0.2,static='true',friction=0.5,density=20 ).setName('Beam'))	lb.addObject(Beam.BeamSprite(x=2171, y=64,width=128,height=128,angle='0' ,restitution=0.2,static='false',friction=0.5,density=5 ))	lb.addObject(Beam.BeamSprite(x=2355, y=64,width=128,height=128,angle='0' ,restitution=0.2,static='false',friction=0.5,density=5 ))	lb.addObject(Beam.BeamSprite(x=2281, y=170,width=164,height=76,angle='0' ,restitution=0.2,static='false',friction=0.5,density=5 ))	lb.render()